import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// Configure axios defaults - dynamically determine backend URL
const getBackendURL = () => {
  // If we have an explicit backend URL from env, use it
  if (process.env.REACT_APP_BACKEND_URL && process.env.REACT_APP_BACKEND_URL !== 'http://localhost:8001') {
    return process.env.REACT_APP_BACKEND_URL;
  }

  // If we're running in production (not localhost), use relative URL
  if (window.location.hostname !== 'localhost' && window.location.hostname !== '127.0.0.1') {
    // Use the same domain as frontend but with API prefix
    return `${window.location.protocol}//${window.location.host}`;
  }

  // Default to localhost for development
  return 'http://localhost:8001';
};

const API_URL = getBackendURL();
console.log('ðŸ”— Backend URL configured:', API_URL);
axios.defaults.baseURL = API_URL;

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem('token'));

  // Set up axios interceptor for token
  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common['Authorization'];
    }
  }, [token]);

  // Check if user is authenticated on app load
  useEffect(() => {
    const checkAuth = async () => {
      const savedToken = localStorage.getItem('token');
      if (savedToken) {
        try {
          const response = await axios.get('/api/auth/me');
          setUser(response.data);
          setToken(savedToken);
        } catch (error) {
          // Token is invalid, remove it
          localStorage.removeItem('token');
          setToken(null);
          setUser(null);
        }
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  const login = async (credentials) => {
    try {
      setLoading(true);
      console.log('ðŸ”‘ Attempting login with:', credentials.email);
      console.log('ðŸŒ Backend URL:', API_URL);

      const formData = new FormData();
      formData.append('username', credentials.email);
      formData.append('password', credentials.password);

      console.log('ðŸ“¤ Making login request to:', `${API_URL}/api/auth/login`);

      const response = await axios.post('/api/auth/login', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('ðŸ“¥ Login response received:', response.status);

      const { access_token, user: userData } = response.data;

      localStorage.setItem('token', access_token);
      setToken(access_token);
      setUser(userData);

      console.log('âœ… Login successful for:', userData.full_name);
      toast.success(`Welcome back, ${userData.full_name}!`);
      return { success: true };
    } catch (error) {
      console.error('âŒ Login error:', error);
      console.error('ðŸ“‹ Error response:', error.response?.data);
      console.error('ðŸ”— Request URL:', error.config?.url);

      const message = error.response?.data?.detail || 'Login failed';
      toast.error(message);
      return { success: false, error: message };
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setLoading(true);
      const response = await axios.post('/api/auth/register', userData);

      const { access_token, user: newUser } = response.data;

      localStorage.setItem('token', access_token);
      setToken(access_token);
      setUser(newUser);

      toast.success(`Welcome to ProjectHub, ${newUser.full_name}!`);
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.detail || 'Registration failed';
      toast.error(message);
      return { success: false, error: message };
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
    delete axios.defaults.headers.common['Authorization'];
    toast.success('Logged out successfully');
  };

  const value = {
    user,
    token,
    loading,
    login,
    register,
    logout,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
