import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { 
  ClipboardDocumentListIcon,
  ExclamationTriangleIcon,
  CurrencyDollarIcon,
  CalendarDaysIcon,
  ChatBubbleLeftRightIcon,
  DocumentTextIcon,
  ArrowRightIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const PlanningIndex = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/projects`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const projectData = await response.json();
        setProjects(projectData);
      } else {
        toast.error('Failed to fetch projects');
      }
    } catch (error) {
      console.error('Error fetching projects:', error);
      toast.error('Error loading projects');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'initiation':
        return 'bg-blue-100 text-blue-800';
      case 'planning':
        return 'bg-yellow-100 text-yellow-800';
      case 'execution':
        return 'bg-green-100 text-green-800';
      case 'monitoring':
        return 'bg-purple-100 text-purple-800';
      case 'closure':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const planningFeatures = [
    {
      name: 'Work Breakdown Structure',
      description: 'Organize project work into manageable tasks and subtasks',
      icon: ClipboardDocumentListIcon,
      status: 'available',
      color: 'text-green-600'
    },
    {
      name: 'Risk Management',
      description: 'Identify, assess, and manage project risks',
      icon: ExclamationTriangleIcon,
      status: 'available',
      color: 'text-green-600'
    },
    {
      name: 'Budget Planning',
      description: 'Plan and track project budget across categories',
      icon: CurrencyDollarIcon,
      status: 'available',
      color: 'text-green-600'
    },
    {
      name: 'Timeline & Gantt',
      description: 'Visual project timeline and dependencies',
      icon: CalendarDaysIcon,
      status: 'available',
      color: 'text-green-600'
    },
    {
      name: 'Communication Plan',
      description: 'Stakeholder communication planning',
      icon: ChatBubbleLeftRightIcon,
      status: 'available',
      color: 'text-green-600'
    },
    {
      name: 'Quality & Procurement',
      description: 'Quality assurance and procurement planning',
      icon: DocumentTextIcon,
      status: 'available',
      color: 'text-green-600'
    }
  ];

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
